import styled from "styled-components"

const DivButtons = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
`;
const Buttons = styled.button`
background-color: #abb8ea;
border: 2px solid #a17cbf;
border-radius: 6px;
margin: 10px;
padding: 5px;
&:hover {
  background-color: #262623c9;
  color: beige;
}
`;

export default function SearchBar({onSearch}) { 
   return (
      <div>
          <input type="search" />
      <button onClick={(id) => {onSearch(id)}}>Agregar</button> 
      </div>
   );
}

export { DivButtons, Buttons }
