export default function SearchBar(props) {
   let characterID;
   const beforeChange = (e) => {
      characterID = e.target.value;
   }
   return (
      <div>
          <input type='search' onBlur={beforeChange} />
      <button onClick={(props.onSearch(characterID))}>Agregar</button> 
      </div>
   );
}
